from django import formsfrom .models import Opportunity, Resource# class InternshipForm(forms.ModelForm):#     class Meta:#         model = Internship#         fields = [#             "title",#             "content",#             "company",#             "start_date",#             "end_date",#             "location",#             "stipend",#             "apply_by",#         ]#         widgets = {#             "start_date": forms.DateInput(attrs={"type": "date"}),#             "end_date": forms.DateInput(attrs={"type": "date"}),#             "apply_by": forms.DateInput(attrs={"type": "date"}),#         }class OpportunityForm(forms.ModelForm):    class Meta:        model = Opportunity        fields = ["title", "description", "company", "location", "apply_by", "stipend"]        widgets = {            "apply_by": forms.DateInput(attrs={"type": "date"}),        }class ResourceForm(forms.ModelForm):    class Meta:        model = Resource        fields = ["title", "content", "url"]from allauth.account.forms import SignupFormfrom django import formsclass CustomSignupForm(SignupForm):    first_name = forms.CharField(max_length=30, label='First Name', required=True)    last_name = forms.CharField(max_length=30, label='Last Name', required=True)        def __init__(self, *args, **kwargs):        super().__init__(*args, **kwargs)        del self.fields['email']  # Remove the email field        def save(self, request):        user = super().save(request)        user.first_name = self.cleaned_data['first_name']        user.last_name = self.cleaned_data['last_name']        user.save()        return user