from django import formsfrom .models import Internship, Opportunity, Resourceclass InternshipForm(forms.ModelForm):	class Meta:		model = Internship		fields = ['title', 'content', 'company', 'start_date', 'end_date', 'location', 'stipend', 'apply_by']		widgets = {			'start_date': forms.DateInput(attrs={'type': 'date'}),			'end_date': forms.DateInput(attrs={'type': 'date'}),			'apply_by': forms.DateInput(attrs={'type': 'date'}),		}class OpportunityForm(forms.ModelForm):	class Meta:		model = Opportunity		fields = ['title', 'content', 'company', 'location', 'apply_by', 'stipend']		widgets = {			'apply_by': forms.DateInput(attrs={'type': 'date'}),		}class ResourceForm(forms.ModelForm):	class Meta:		model = Resource		fields = ['title', 'content', 'url']