from django.conf import settingsfrom django.urls import pathfrom django.conf.urls.static import staticfrom app import viewsurlpatterns = [    path("", views.home, name="index"),    path("profile/<str:username>/", views.profile, name="profile"),    path("opportunities/", views.opportunity_list, name="opportunity_list"),    path(        "opportunities/<int:pk>/", views.opportunity_detail, name="opportunity_detail"    ),    path("resources", views.resource_list, name="resource_list"),] + static(settings.STATIC_URL, document_root=settings.STATIC_ROOT)